'            .time-block:hover {',
'                filter: brightness(90%);',
'            }',
'        </style>',
'        <div class="schedule-container">'';',
'',
'    FOR record IN (',
'        WITH zakres AS (',
'            SELECT ',
'                GODZINAOD,',
'                GODZINADO',
'            FROM ',
'                DOSTEPNOSC',
'            WHERE ',
'                WYPOSAZENIEID = :P52_WYPOSAZENIEID',
'                AND DATA = :P52_DATAREZERWACJI',
'        ),',
'        przedzialy AS (',
'            SELECT ',
'                GODZINAOD + (LEVEL - 1) * 0.50 + ',
